    $HCW.daysInMonth = function(mnth, year){
        var d = new Date(year, mnth, 0).getDate();
        return d;
    }
    
    $HCW.setDayVals = function(hotel_day, dayhelptext, dayNames, month, year, hotel_dayVal){
        var month;
        var dt = new Date();
        var o = '<option value="-1">' + dayhelptext  + '</option>';
        for(var i=1;i<=$HCW.daysInMonth(parseInt(month), parseInt(year));i++){
            dt.setFullYear(year, month-1, i);
            o += '<option value="' + i + '">' + dayNames[parseInt(dt.getDay())] + ' ' + i + '</option>';
        }
        hotel_day.html(o);
        hotel_day.val(hotel_dayVal);
    }
    
    $HCW.checkInOutChanged = function(that, checkInChanged){
        var monthChanged = $HCW(that).attr('class').indexOf('month') != -1;
        var theWidget = $HCW(that).parents('div.hcw_widget');
        var theId = theWidget[0].id;
        var hotelCheckin_day = theWidget.find('select.hcw_hotelCheckin_day');
        var hotelCheckin_dayVal = hotelCheckin_day.val();
        var hotelCheckin_my = theWidget.find('select.hcw_hotelCheckin_month');
        var hotelCheckin_myVal = hotelCheckin_my.val();
        var hotelCheckin_my = theWidget.find('select.hcw_hotelCheckin_month');
        var hotelCheckin_myVal = hotelCheckin_my.val();
        
        var hotelCheckout_day = theWidget.find('select.hcw_hotelCheckout_day');
        var hotelCheckout_dayVal = hotelCheckout_day.val();        
        var hotelCheckout_my = theWidget.find('select.hcw_hotelCheckout_month');
        var hotelCheckout_myVal = hotelCheckout_my.val();
        
        var checkin = hotelCheckin_myVal + '-' + hotelCheckin_dayVal;
        var checkout = hotelCheckout_myVal + '-' + hotelCheckout_dayVal;
        
        var scheckin = checkin.split('-');
        var scheckout = checkout.split('-');
        var haveGoodCheckin = (scheckin.length == 3) && scheckin[0] && scheckin[1] && scheckin[2];
        var haveGoodCheckout = (scheckout.length == 3) && scheckout[0] && scheckout[1] && scheckout[2];
        var checkinDate = new Date();
        var checkoutDate = new Date();
        var langCode = $HCW.widgetOptions[theId].languagecode.toLowerCase();

        var dayNames;
        if(!langCode || (langCode == 'en')){
            dayNames = $HCW.datepicker.regional[''].dayNamesShort;
        } else {
            dayNames = $HCW.datepicker.regional[langCode].dayNamesShort;      
        }

        if(monthChanged && checkInChanged){
            var smy = hotelCheckin_myVal.split('-');
            if(smy.length > 0){            
                $HCW.setDayVals(hotelCheckin_day, $HCW.widgetOptions[theId].dayhelptext, dayNames, smy[1], smy[0], hotelCheckin_dayVal);
            }
        }
        if(monthChanged && !checkInChanged){
            var smy = hotelCheckout_myVal.split('-');
            if(smy.length > 0){
                $HCW.setDayVals(hotelCheckout_day, $HCW.widgetOptions[theId].dayhelptext, dayNames, smy[1], smy[0], hotelCheckout_dayVal);
            }
        }
        
        if(checkInChanged && haveGoodCheckin){
            checkinDate.setFullYear(scheckin[0], scheckin[1], scheckin[2]);
            if(haveGoodCheckout){ checkoutDate.setFullYear(scheckout[0], scheckout[1], scheckout[2]); }
            if(!haveGoodCheckout || (checkoutDate < checkinDate)){
                // Change the checkout date
                if(hotelCheckin_dayVal < $HCW.daysInMonth(parseInt(scheckin[1]), parseInt(scheckin[0]))){
                    hotelCheckout_my.val(hotelCheckin_myVal);
                    $HCW.setDayVals(hotelCheckout_day, $HCW.widgetOptions[theId].dayhelptext, dayNames, scheckin[1], scheckin[0], hotelCheckout_dayVal);
                    hotelCheckout_day.val(parseInt(hotelCheckin_dayVal) + 1);         
                } else {
                    var idx = $HCW.inArray(hotelCheckin_myVal, $HCW.yearMonthList);
                    hotelCheckout_my.val($HCW.yearMonthList[idx+1]);
                    var syml = $HCW.yearMonthList[idx+1].split('-');
                    $HCW.setDayVals(hotelCheckout_day, $HCW.widgetOptions[theId].dayhelptext, dayNames, syml[1], syml[0], hotelCheckout_dayVal);
                    hotelCheckout_day.val(1);
                }
            }
            theWidget.find('hcw_hiddenCheckin').val(checkin);
            theWidget.find('hcw_hiddenCheckout').datepicker("option", "minDate", new Date(scheckin[0], scheckin[1], parseInt(scheckin[2]) + 1));
        } else if(!checkInChanged && haveGoodCheckout){
            checkoutDate.setFullYear(scheckout[0], scheckout[1], scheckout[2]);
            if(haveGoodCheckin){ checkinDate.setFullYear(scheckin[0], scheckin[1], scheckin[2]); }
            if(!haveGoodCheckin || (checkinDate > checkoutDate)){
                // Change the checkin date
                if(hotelCheckout_dayVal > 1){
                    hotelCheckin_my.val(hotelCheckout_myVal);
                    $HCW.setDayVals(hotelCheckin_day, $HCW.widgetOptions[theId].dayhelptext, dayNames, scheckout[1], scheckout[0], hotelCheckin_dayVal);
                    hotelCheckin_day.val(parseInt(hotelCheckout_dayVal) - 1); 
                } else {
                    var idx = $HCW.inArray(hotelCheckout_myVal, $HCW.yearMonthList);
                    hotelCheckin_my.val($HCW.yearMonthList[idx-1]);
                    var syml = $HCW.yearMonthList[idx-1].split('-');
                    $HCW.setDayVals(hotelCheckin_day, $HCW.widgetOptions[theId].dayhelptext, dayNames, syml[1], syml[0], hotelCheckin_dayVal);
                    hotelCheckin_day.val($HCW.daysInMonth(parseInt(syml[1]), parseInt(syml[0])));
                }
            }
            theWidget.find('hcw_hiddenCheckout').val(checkout);
            theWidget.find('hcw_hiddenCheckin').datepicker("option", "maxDate", checkout);            
        }
    }
    
