    $HCW.writePopularCities = function (openInNewWindow, expandThem, urlParams){
        var o = '<div class="hcw_popularCitiesContainer" style="display: none;"><ul>';
        var targetText = '';
        var cup = $HCW.extend({}, urlParams);        
        if(cup.affiliateParam){
            cup.affiliateParam = '?' + cup.affiliateParam.substring(1);
        } else if(cup.brandParam){
            cup.brandParam = '?' + cup.brandParam.substring(1);            
        } else if(cup.labelParam){
            cup.labelParam = '?' + cup.labelParam.substring(1);            
        } else if(cup.countryCode){
            cup.countryCode = '?' + cup.countryCode.substring(1);            
        } else if(cup.languageCode){
            cup.languageCode = '?' + cup.languageCode.substring(1);                        
        } 
        if(openInNewWindow){ targetText = 'target="_top" '; }
        for(var _hcw_city in $HCW.widget_options.popularcities){ 
            if(expandThem){
                _hcw_link = cup.host + '/City/' + $HCW.widget_options.popularcities[_hcw_city] + '.htm' + cup.affiliateParam + cup.brandParam + cup.labelParam + cup.countryCode + cup.languageCode;
            } else {
                _hcw_link = $HCW.widget_options.popularcities[_hcw_city] + cup.affiliateParam + cup.brandParam + cup.labelParam + cup.countryCode + cup.languageCode;
            }
            o += '<li><a ' + targetText + 'href="' + _hcw_link + '">' + _hcw_city + '</a></li>'; 
        }        
        o += '</ul></div>';
        return o;
    }
    $HCW.initOrWait = function(widgetId){
        var ourWidgetId = widgetId;
        var ciSize;
        if($HCW.widgetOptions[widgetId].usepulldowndates){
            ciSize = parseInt($HCW('#' + widgetId + ' select.hcw_hotelCheckin_day').css('height'));
        } else {
            ciSize = parseInt($HCW('#' + widgetId + ' input.hcw_hotelCheckin').css('width'));
        }
        $HCW('#' + ourWidgetId).css({'display': 'block'});        
        if(ciSize > 0){
            $HCW.initWidget(ourWidgetId);
        } else {
            setTimeout(function(){
                $HCW.initOrWait(ourWidgetId);
            }, 500);
        }   
    }
    
